red='\x1b[0;31m'; yellow='\x1b[0;33m'; green='\x1b[0;32m'; teal='\x1b[0;36m'; blue='\x1b[0;34m'; purple='\x1b[0;35m'; NC='\x1b[0m'; echo -e "\n\tWP Engine DNS Assistance Tool\n\tType \"exit\" or \"quit\" to exit...\n\n"; while [ true ]; do echo -en "${red}Domain:${NC}"; read dom; dom=$(echo "$dom" | sed "s_[^a-zA-Z0-9.-]__g" | sed "s_http__g") ; echo -e "\n\t ${yellow}A records${NC}\n\t------------\n $(dig +short $dom | while read line; do echo -e "${green}\t$line${NC}"; done)\n\n \t${yellow}Nameservers${NC}\n\t--------------\n $(dig +short NS $dom | while read line; do echo -e "\t${green}$line${NC}"; done)"; done;
