dns(){ bold='\033[1m'; red='\x1b[0;31m'; yellow='\x1b[0;33m'; green='\x1b[0;32m'; teal='\x1b[0;36m'; blue='\x1b[0;34m'; purple='\x1b[0;35m'; NC='\x1b[0m'; if [[ ! -z "$1" ]]; then dom="$1"; else echo -en "\n${red}Domain: ${NC}"; read dom; fi; doms=$(echo "$dom" | sed "s_[^a-zA-Z0-9.-]__g" | sed -r "s_https?(www\.)?__g" | sed "s,/$,,"); if [[ ! -z $doms ]]; then echo -e "\n$(whois $doms | egrep '(Domain Name: |Registrar: |Updated Date: )' | sort | awk -F':' ' { print $1"\t"$2} ' |  sed 's/^/\t\t/')\n\n\n------------------------------------------------------------------\n${bold}Domain\t\t\t\tType\tRecord Found${NC}\n------------------------------------------------------------------\n\n$(dig $doms -t NS +noall +answer $doms -t A +noall +answer www."$dom" -t A +noall +answer cdn."$doms" +noall +answer -t A | tail -n +4 | awk -v pfblue='\033[34m' -v pfNC='\033[0m' {'printf ("\033[32;1m%30-s\033[0m\t\033[37m%-4s\033[0m\t\033[32;1m%s\033[0m\n", $1, $4, $5)'})\n"; else echo -e "\n${yellow}No domain name was provided.${NC}\n\n"; fi;};
